import styled from "styled-components";

export const Container  = styled.div`
    display: flex;
    flex-direction: row;    
    align-items: center;
    width: 100%;
    background-color: #f0f0f0;


    .CookingImg{
        width: 45vw;
        height: 85vh;
        margin-left: 20px;
    }
    
`;
